import App from "./App"


const Task = (props)=>{
    return(
        <div className="task"
        style={{backgroundColor: props.completed? 'green': 'red'}}>
            <h1>{props.taskName}</h1>
            <div className="buttons">
                <button onClick={()=>props.completeTask(props.id)}>Complete</button>
                <button onClick={()=>props.deleteTask(props.id)}>X</button>
            </div>
        </div>
    )
}

export default Task;
